import React from 'react';

function ConstellationInfo() {
    const constellations = [
        {
            name: "Orion",
            keyStars: "Betelgeuse, Rigel",
            description: "Orion is one of the most recognizable constellations in the night sky, named after a hunter in Greek mythology.",
            mythology: "Orion was a giant huntsman whom Zeus placed among the stars.",
            bestViewing: "Best viewed in winter.",
            deepSkyObjects: "Includes the Orion Nebula (M42)."
        },
        {
            name: "Ursa Major",
            keyStars: "Dubhe, Merak",
            description: "Ursa Major, also known as the Great Bear, contains the famous Big Dipper asterism.",
            mythology: "Represents the bear in Greek mythology.",
            bestViewing: "Best viewed in spring.",
            deepSkyObjects: "Contains the Pinwheel Galaxy (M101)."
        },
        {
            name: "Cassiopeia",
            keyStars: "Schedar, Caph",
            description: "Cassiopeia is easily recognizable due to its distinctive 'W' shape and is named after a queen in Greek mythology.",
            mythology: "Cassiopeia was known for her beauty and arrogance.",
            bestViewing: "Best viewed in fall.",
            deepSkyObjects: "Includes the Bubble Nebula (NGC 7635)."
        },
        {
            name: "Scorpius",
            keyStars: "Antares, Shaula",
            description: "Scorpius is known for its bright red supergiant star, Antares, and is associated with the scorpion in mythology.",
            mythology: "Scorpius represents the scorpion that killed Orion.",
            bestViewing: "Best viewed in summer.",
            deepSkyObjects: "Includes the Lagoon Nebula (M8)."
        },
        {
            name: "Leo",
            keyStars: "Regulus, Denebola",
            description: "Leo represents a lion and is one of the twelve zodiac constellations.",
            mythology: "Leo is associated with the Nemean Lion, which Hercules defeated.",
            bestViewing: "Best viewed in spring.",
            deepSkyObjects: "Contains the Leo Triplet galaxies."
        },
        {
            name: "Taurus",
            keyStars: "Aldebaran, Elnath",
            description: "Taurus is depicted as a bull and is prominent in the winter sky.",
            mythology: "Linked to several myths, including that of Zeus transforming into a bull.",
            bestViewing: "Best viewed in winter.",
            deepSkyObjects: "Includes the Pleiades star cluster (M45)."
        },
        {
            name: "Aquila",
            keyStars: "Altair",
            description: "Aquila is known as the eagle and contains the bright star Altair.",
            mythology: "In mythology, Aquila is often associated with Zeus's eagle.",
            bestViewing: "Best viewed in summer.",
            deepSkyObjects: "Includes the Dumbbell Nebula (M27)."
        },
        {
            name: "Sagittarius",
            keyStars: "Kaus Australis, Kaus Media",
            description: "Sagittarius is represented as an archer and is rich in star clusters and nebulae.",
            mythology: "Often identified with the centaur Chiron.",
            bestViewing: "Best viewed in summer.",
            deepSkyObjects: "Includes the Galactic Core and the Lagoon Nebula (M8)."
        },
        {
            name: "Virgo",
            keyStars: "Spica",
            description: "Virgo is one of the largest constellations and represents a maiden.",
            mythology: "Associated with several goddesses of harvest.",
            bestViewing: "Best viewed in spring.",
            deepSkyObjects: "Contains the Virgo Cluster of galaxies."
        }
    ];

    return (
        <div>
            <h2>Constellation Information</h2>
            <p>Information about different constellations will be displayed here:</p>
            <ul>
                {constellations.map((constellation, index) => (
                    <li key={index}>
                        <h3>{constellation.name}</h3>
                        <p><strong>Key Stars:</strong> {constellation.keyStars}</p>
                        <p>{constellation.description}</p>
                        <p><strong>Mythology:</strong> {constellation.mythology}</p>
                        <p><strong>Best Viewing:</strong> {constellation.bestViewing}</p>
                        <p><strong>Deep Sky Objects:</strong> {constellation.deepSkyObjects}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ConstellationInfo;
