import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import '../components/Header.css';

function Home() {
    const [stars, setStars] = useState([]);
    
    const openUrl=()=> {
        
        window.open("http://localhost:7000", "_blank");
    }
    

    useEffect(() => {
        const fetchStars = async () => {
            try {
                const response = await axios.get('/api/stars');
                setStars(response.data);
            } catch (err) {
                console.error("Error fetching stars:", err);
            }
        };
        fetchStars();
    }, []);

    return (
        <>
        <div>
            <h2>Welcome to Starlight Tracker</h2>
            <h3>Famous Stars</h3>
            <ul>
                {stars.map(star => (
                    <li key={star.id}>{star.name}</li>
                ))}
            </ul>
            <nav className="nav">
                    
                    <div style={{ position: 'absolute', top: '10px', right: '100px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
    
    <button className='btn btn-secondary position-absolute top-100 start-0 translate-middle' onClick={openUrl}>Upload / Capture</button>
</div>

                    
                </nav>
        </div>
        </>
    );
}

export default Home;
