import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import '../components/Header.css';

function Home() {
    const [stars, setStars] = useState([]);
    
    const openUrl=()=> {
        
        window.open("http://localhost:7000", "_blank");
    }
    

    useEffect(() => {
        const fetchStars = async () => {
            try {
                const response = await axios.get('/api/stars');
                setStars(response.data);
            } catch (err) {
                console.error("Error fetching stars:", err);
            }
        };
        fetchStars();
    }, []);

    return (
        <>
        
            <h2 className='h2 position-absolute top-50 start-50 translate-middle my-5'>Welcome to Starlight Tracker</h2>
            <h3 className='h3'>Famous Stars</h3>
            <ul>
                {stars.map(star => (
                    <li key={star.id}>{star.name}</li>
                ))}
            </ul>
            
                    
                    
    
    <button className='btn btn-secondary position-absolute top-55 end-0 translate-middle-y' onClick={openUrl}>Upload / Capture</button>

                    
                
    
        </>
    );
}

export default Home;
